mapper.select:
select id, title, content, creator_id, is_pinned, pin_time, create_time
from sh_announcement
where id = #{id,jdbcType=BIGINT}

mapper.update:
update sh_announcement
title = #{title,jdbcType=VARCHAR}, content = #{content,jdbcType=VARCHAR}, is_pinned = #{isPinned,jdbcType=TINYINT}, pin_time = #{pinTime,jdbcType=TIMESTAMP}, creator_id = #{creatorId,jdbcType=BIGINT},
where id = #{id,jdbcType=BIGINT}
