import AppHead from "../common/AppHeader.vue";
import AppBody from "../common/AppPageBody.vue";
import AppFoot from "../common/AppFoot.vue"; // import options from '../common/country-data.js' // 已移除，不再使用级联选择器

export default {
  name: "me",
  components: {
    AppHead: AppHead,
    AppBody: AppBody,
    AppFoot: AppFoot
  },
  data: function data() {
    return {
      imgFileList: [],
      addressInfo: {
        consigneeName: '',
        consigneePhone: '',
        provinceName: '',
        cityName: '',
        regionName: '',
        detailAddress: '',
        defaultFlag: false
      },
      activeName: '1',
      handleName: ['下架', '删除', '取消购物车', '', ''],
      dataList: [[], [], [], [], []],
      orderStatus: ['待付款', '待发货', '待收货', '已完成', '已取消'],
      userInfoDialogVisible: false,
      notUserNicknameEdit: true,
      userPasswordEdit: false,
      userPassword1: '',
      userPassword2: '',
      userPassword3: '',
      eidtAddress: false,
      // selectedOptions和options已移除，现在直接使用输入框
      userInfo: {
        accountNumber: "",
        avatar: "",
        nickname: "",
        signInTime: "",
        userRole: 0
