        detailAddress: [{
          required: true,
          message: '请输入收货地址',
          trigger: 'blur'
        }, {
          min: 5,
          message: '收货地址不能少于5个字符',
          trigger: 'blur'
        }, {
          max: 100,
          message: '收货地址不能超过100个字符',
          trigger: 'blur'
        }]
      }
