  methods: {
    // 头像上传前验证
    beforeAvatarUpload: function beforeAvatarUpload(file) {
      var isImage = file.type.startsWith('image/');
      var isLt2M = file.size / 1024 / 1024 < 2;

      if (!isImage) {
        this.$message.error('上传头像只能是图片格式!');
        return false;
      }

      if (!isLt2M) {
        this.$message.error('上传头像大小不能超过 2MB!');
        return false;
      }

      return true;
    },
    // 滚动到添加地址区域
    scrollToAddAddress: function scrollToAddAddress() {
      document.querySelector('.new-address-card').scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
    },
    loadUserInfo: function loadUserInfo() {
      var _this = this;

      this.$api.getUserInfo().then(function (res) {
        if (res.status_code === 1) {
          res.data.signInTime = res.data.signInTime.substring(0, 10); // 强制更新全局用户信息

          _this.$globalData.userInfo = res.data; // 更新当前页面的用户信息

          _this.userInfo = res.data;
        }
      }).catch(function () {
        // 如果获取失败，尝试从全局数据加载，以防万一
        _this.userInfo = _this.$globalData.userInfo;
      });
    },
    getEmptyIcon: function getEmptyIcon(tabName) {
      var icons = {
        '1': 'el-icon-upload',
        '2': 'el-icon-download',
        '3': 'el-icon-shopping-cart-full',
        '4': 'el-icon-sold-out',
        '5': 'el-icon-shopping-bag-1'
      };
      return icons[tabName] || 'el-icon-document';
    },
    getEmptyText: function getEmptyText(tabName) {
      var texts = {
        '1': '您还没有发布任何商品',
        '2': '没有已下架的商品',
        '3': '您的购物车是空的',
        '4': '您还没有出售记录',
        '5': '您还没有购买记录'
      };
      return texts[tabName] || '暂无数据';
    },
    getMyFavorite: function getMyFavorite() {
      var _this2 = this;

      this.$api.getMyFavorite().then(function (res) {
        console.log('getMyFavorite', res);

        if (res.status_code === 1) {
          for (var i = 0; i < res.data.length; i++) {
            if (res.data[i].idleItem && res.data[i].idleItem.pictureList) {
              var pictureList = JSON.parse(res.data[i].idleItem.pictureList);

              _this2.dataList[2].push({
                favoriteId: res.data[i].id,
                id: res.data[i].idleItem.id,
                imgUrl: pictureList.length > 0 ? pictureList[0] : '',
                idleName: res.data[i].idleItem.idleName,
                idleDetails: res.data[i].idleItem.idleDetails,
                timeStr: res.data[i].createTime.substring(0, 10) + " " + res.data[i].createTime.substring(11, 19),
                idlePrice: res.data[i].idleItem.idlePrice
              });
            }
          }
        }
      });
    },
    getMySoldIdle: function getMySoldIdle() {
      var _this3 = this;

      this.$api.getMySoldIdle().then(function (res) {
        if (res.status_code === 1) {
          console.log('getMySoldIdle', res.data);

          for (var i = 0; i < res.data.length; i++) {
            if (res.data[i].idleItem && res.data[i].idleItem.pictureList) {
              var pictureList = JSON.parse(res.data[i].idleItem.pictureList);

              _this3.dataList[3].push({
                id: res.data[i].id,
                imgUrl: pictureList.length > 0 ? pictureList[0] : '',
                idleName: res.data[i].idleItem.idleName,
                idleDetails: res.data[i].idleItem.idleDetails,
                timeStr: res.data[i].createTime.substring(0, 10) + " " + res.data[i].createTime.substring(11, 19),
                idlePrice: res.data[i].orderPrice,
                orderStatus: res.data[i].orderStatus
              });
            }
          }
        }
      });
    },
    getMyOrder: function getMyOrder() {
      var _this4 = this;

      this.$api.getMyOrder().then(function (res) {
        if (res.status_code === 1) {
          console.log('getMyOrder', res.data);

          for (var i = 0; i < res.data.length; i++) {
            if (res.data[i].idleItem && res.data[i].idleItem.pictureList) {
              var pictureList = JSON.parse(res.data[i].idleItem.pictureList);

              _this4.dataList[4].push({
                id: res.data[i].id,
                imgUrl: pictureList.length > 0 ? pictureList[0] : '',
                idleName: res.data[i].idleItem.idleName,
                idleDetails: res.data[i].idleItem.idleDetails,
                timeStr: res.data[i].createTime.substring(0, 10) + " " + res.data[i].createTime.substring(11, 19),
                idlePrice: res.data[i].orderPrice,
                orderStatus: res.data[i].orderStatus
              });
            }
          }
        }
      });
    },
    getIdleItemData: function getIdleItemData() {
      var _this5 = this;

      this.$api.getAllIdleItem().then(function (res) {
        console.log(res);

        if (res.status_code === 1) {
          for (var i = 0; i < res.data.length; i++) {
            res.data[i].timeStr = res.data[i].releaseTime.substring(0, 10) + " " + res.data[i].releaseTime.substring(11, 19);
            var pictureList = JSON.parse(res.data[i].pictureList);
            res.data[i].imgUrl = pictureList.length > 0 ? pictureList[0] : '';

            if (res.data[i].idleStatus === 1) {
              _this5.dataList[0].push(res.data[i]);
            } else if (res.data[i].idleStatus === 2) {
              _this5.dataList[1].push(res.data[i]);
            }
          }
        }
      });
    },
    getAddressData: function getAddressData() {
      var _this6 = this;

      this.$api.getAddress().then(function (res) {
        if (res.status_code === 1) {
          var data = res.data;

          for (var i = 0; i < data.length; i++) {
            data[i].detailAddressText = data[i].provinceName + data[i].cityName + data[i].regionName + data[i].detailAddress;
            data[i].defaultAddress = data[i].defaultFlag ? '默认地址' : '设为默认';
          }

          console.log(data);
          _this6.addressData = data;
        }
      });
    },
    handleClick: function handleClick(tab, event) {
      console.log(this.activeName);
    },
    saveUserNickname: function saveUserNickname() {
      var _this7 = this;

      this.notUserNicknameEdit = true;
      this.$api.updateUserPublicInfo({
        nickname: this.userInfo.nickname
      }).then(function (res) {
        console.log(res);
        _this7.$globalData.userInfo.nickname = _this7.userInfo.nickname;

        _this7.$message.success('昵称修改成功！');
      });
    },
    savePassword: function savePassword() {
      var _this8 = this;

      if (!this.userPassword1 || !this.userPassword2) {
        this.$message.error('密码不能为空！');
        return;
      }

      if (this.userPassword2.length < 6 || this.userPassword2.length > 20) {
        this.$message.error('新密码长度必须在6-20位之间！');
        return;
      }

      if (this.userPassword2 === this.userPassword3) {
        this.$api.updatePassword({
          oldPassword: this.userPassword1,
          newPassword: this.userPassword2
        }).then(function (res) {
          if (res.status_code === 1) {
            _this8.userPasswordEdit = false;

            _this8.$message({
              message: '修改成功！',
              type: 'success'
            });

            _this8.userPassword1 = '';
            _this8.userPassword2 = '';
            _this8.userPassword3 = '';
          } else {
            _this8.$message.error('旧密码错误，修改失败！');
          }
        });
      } else {
        this.$message.error('两次输入的密码不一致！');
      }
    },
    finishEdit: function finishEdit() {
      this.notUserNicknameEdit = true;
      this.userInfoDialogVisible = false;
      this.userPasswordEdit = false;
    },
    // handleAddressChange方法已移除，现在直接使用输入框
    handleEdit: function handleEdit(index, row) {
      console.log(index, row);
      this.addressInfo = JSON.parse(JSON.stringify(row)); // selectedOptions已移除，现在直接使用detailAddress

      this.scrollToAddAddress();
    },
    handleDelete: function handleDelete(index, row) {
      var _this9 = this;

      console.log(index, row);
      this.$confirm('是否确定删除该地址?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this9.$api.deleteAddress(row).then(function (res) {
          if (res.status_code === 1) {
            _this9.$message({
              message: '删除成功！',
              type: 'success'
            });

            _this9.addressData.splice(index, 1);

            if (row.defaultFlag && _this9.addressData.length > 0) {
              _this9.addressData[0].defaultFlag = true;
              _this9.addressData[0].defaultAddress = '默认地址';

              _this9.update({
                id: _this9.addressData[0].id,
                defaultFlag: true
              });
            }
          } else {
            _this9.$message.error('系统异常，删除失败！');
          }
        }).catch(function () {
          _this9.$message.error('网络异常！');
        });
      }).catch(function () {});
    },
    handleSetDefault: function handleSetDefault(index, row) {
      var _this10 = this;

      console.log(index, row); // 取消其他地址的默认状态

      this.addressData.forEach(function (item) {
        if (item.defaultFlag) {
          item.defaultFlag = false;

          _this10.update({
            id: item.id,
            defaultFlag: false
          });
        }
      });
      row.defaultFlag = true;
      this.update(row);
    },
    toDetails: function toDetails(activeName, item) {
      if (activeName === '4' || activeName === '5') {
        this.$router.push({
          path: '/order',
          query: {
            id: item.id
          }
        });
      } else {
        this.$router.push({
          path: '/details',
          query: {
            id: item.id
          }
        });
      }
    },
    handle: function handle(activeName, item, index) {
      var _this11 = this;

      console.log(activeName, item, index);
      var confirmText = '';
      if (activeName === '1') confirmText = '是否确认下架该商品？';else if (activeName === '2') confirmText = '是否确认删除该商品？';else if (activeName === '3') confirmText = '是否确认取消收藏该商品？';
      this.$confirm(confirmText, '提示', {
        confirmButtonText: '确认',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        if (activeName === '1') {
          _this11.$api.updateIdleItem({
            id: item.id,
            idleStatus: 2
          }).then(function (res) {
            console.log(res);

            if (res.status_code === 1) {
              _this11.dataList[0].splice(index, 1);

              item.idleStatus = 2;

              _this11.dataList[1].unshift(item);

              _this11.$message.success('商品已下架！');
            } else {
              _this11.$message.error(res.msg);
            }
          });
        } else if (activeName === '2') {
          _this11.$api.updateIdleItem({
            id: item.id,
            idleStatus: 0
          }).then(function (res) {
            console.log(res);

            if (res.status_code === 1) {
              _this11.dataList[1].splice(index, 1);

              _this11.$message.success('商品已删除！');
            } else {
              _this11.$message.error(res.msg);
            }
          });
        } else if (activeName === '3') {
          _this11.$api.deleteFavorite({
            id: item.favoriteId
          }).then(function (res) {
            console.log(res);

            if (res.status_code === 1) {
              _this11.$message({
                message: '已取消购物车！',
                type: 'success'
              });

              _this11.dataList[2].splice(index, 1);
            } else {
              _this11.$message.error(res.msg);
            }
          }).catch(function (e) {});
        }
      }).catch(function () {});
    },
    fileHandleSuccess: function fileHandleSuccess(response, file, fileList) {
      var _this12 = this;

      console.log("file:", response, file, fileList);
      var imgUrl = response.data;
      this.imgFileList = [];
      this.$api.updateUserPublicInfo({
        avatar: imgUrl
      }).then(function (res) {
        console.log(res);
        _this12.userInfo.avatar = imgUrl;
        _this12.$globalData.userInfo.avatar = imgUrl;

        _this12.$message.success('头像上传成功！');
      });
    },
    update: function update(data) {
      var _this13 = this;

      this.$api.updateAddress(data).then(function (res) {
        if (res.status_code === 1) {
          _this13.getAddressData();

          _this13.$message({
            message: '修改成功！',
            type: 'success'
          });
        } else {
          _this13.$message.error('系统异常，修改失败！');
        }
      }).catch(function () {
        _this13.$message.error('网络异常！');
      });
    },
    saveAddress: function saveAddress() {
      var _this14 = this;

      // 表单验证
      this.$refs.addressForm.validate(function (valid) {
        if (!valid) {
          return false;
        }

        if (_this14.addressInfo.id) {
          console.log('update:', _this14.addressInfo);

          _this14.update(_this14.addressInfo);

          _this14.resetAddressForm();
        } else {
          if (_this14.addressData.length >= 5) {
            _this14.$message.error('已达到最大地址数量！');
          } else {
            console.log(_this14.addressInfo);

            _this14.$api.addAddress(_this14.addressInfo).then(function (res) {
              if (res.status_code === 1) {
                _this14.getAddressData();

                _this14.$message({
                  message: '新增成功！',
                  type: 'success'
                });

                _this14.resetAddressForm();
              } else {
                _this14.$message.error('系统异常，新增失败！');
              }
            }).catch(function (e) {
              _this14.$message.error('网络异常！');
            });
          }
        }
      });
    },
    resetAddressForm: function resetAddressForm() {
      this.addressInfo = {
        consigneeName: '',
        consigneePhone: '',
        provinceName: '',
        cityName: '',
        regionName: '',
        detailAddress: '',
        defaultFlag: false
      }; // selectedOptions已移除

      if (this.$refs.addressForm) {
        this.$refs.addressForm.resetFields();
      }
    },
    goToMerchantApply: function goToMerchantApply() {
      this.$router.push('/merchant/apply');
    },
    goToMerchantManage: function goToMerchantManage() {
      this.$router.push('/merchant/manage');
    },
    goToMembership: function goToMembership() {
      this.$router.push('/membership');
    },
    goToAutoReply: function goToAutoReply() {
      var _this15 = this;

      console.log('点击自动回复按钮，准备跳转到 /auto-reply'); // 直接使用push，不使用replace

      var targetPath = '/auto-reply';
      console.log('准备跳转到:', targetPath); // 先检查路由是否存在

      var route = this.$router.resolve(targetPath);
      console.log('路由解析结果:', route); // 执行跳转

      this.$router.push(targetPath).then(function () {
        console.log('路由跳转成功，当前路径:', _this15.$route.path);
      }).catch(function (err) {
        console.error('路由跳转失败:', err);
        console.error('错误详情:', err.message, err.stack);

        _this15.$message.error('页面跳转失败: ' + (err.message || '未知错误'));
      });
    }
  }
};
