export default {
  name: 'DisputeManagement',
  data: function data() {
    return {
      disputes: [],
      afterSales: [],
      allItems: [],
      // 合并后的列表
      loading: false,
      currentPage: 1,
      pageSize: 8,
      total: 0,
      filterStatus: null,
      // null=全部, 0=待处理, 2=已解决
      filterType: null,
      // null=全部, 'dispute'=交易纠纷, 'afterSale'=售后申请
      dialogVisible: false,
      currentItem: null,
      // 当前查看的项目（可能是纠纷或售后）
      handleForm: {
        handleResult: 1,
        handleDescription: ''
      }
    };
  },
  created: function created() {
    this.loadAllData();
  },
  filters: {
    formatDate: function formatDate(dateStr) {
      if (!dateStr) return '';
      var date = new Date(dateStr);
      return date.toLocaleString('zh-CN', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    }
  },
  methods: {
    loadAllData: function loadAllData() {
      var _this = this;

      this.loading = true; // 同时加载交易纠纷和售后申请

      Promise.all([this.loadDisputes(), this.loadAfterSales()]).then(function () {
        _this.mergeItems();
      }).finally(function () {
        _this.loading = false;
      });
    },
    loadDisputes: function loadDisputes() {
      var _this2 = this;

      return this.$api.adminGetDisputeList({
        status: this.filterStatus,
        page: this.currentPage,
        nums: this.pageSize
      }).then(function (res) {
        console.log('加载纠纷列表响应:', res);

        if (res.status_code === 1) {
          _this2.disputes = (res.data.list || []).map(function (item) {
            item.type = 'dispute';
            return item;
          });
          console.log('纠纷列表:', _this2.disputes);
        } else {
          console.warn('加载纠纷列表失败:', res.msg);
          _this2.disputes = [];
        }
      }).catch(function (err) {
        console.error('加载纠纷列表失败', err);
        _this2.disputes = [];
      });
    },
    loadAfterSales: function loadAfterSales() {
      var _this3 = this;

      return this.$api.getAdminPendingAfterSales({}).then(function (res) {
        console.log('加载售后申请响应:', res);

        if (res.status_code === 1) {
          _this3.afterSales = (res.data || []).map(function (item) {
            item.type = 'afterSale';
            return item;
          });
          console.log('售后申请列表:', _this3.afterSales);
        } else {
          console.warn('加载售后申请失败:', res.msg);
          _this3.afterSales = [];
        }
      }).catch(function (err) {
        console.error('加载售后申请失败', err);
        _this3.afterSales = [];
      });
    },
    mergeItems: function mergeItems() {
      var items = [];
      console.log('合并数据 - 纠纷数量:', this.disputes.length, '售后数量:', this.afterSales.length);
      console.log('当前筛选条件 - 类型:', this.filterType, '状态:', this.filterStatus); // 添加交易纠纷

      if (!this.filterType || this.filterType === 'dispute') {
        items = items.concat(this.disputes);
      } // 添加售后申请


      if (!this.filterType || this.filterType === 'afterSale') {
        items = items.concat(this.afterSales);
      }

      console.log('合并后数量（筛选前）:', items.length); // 按状态筛选

      if (this.filterStatus !== null) {
        if (this.filterStatus === 0) {
          // 待处理：纠纷状态为0，售后状态为3或4
          items = items.filter(function (item) {
            if (item.type === 'dispute') {
              return item.disputeStatus === 0;
            } else {
              return item.applicationStatus === 3 || item.applicationStatus === 4;
            }
          });
        } else if (this.filterStatus === 2) {
          // 已解决：纠纷状态为2，售后状态为5或6
          items = items.filter(function (item) {
            if (item.type === 'dispute') {
              return item.disputeStatus === 2;
            } else {
              return item.applicationStatus === 5 || item.applicationStatus === 6;
            }
          });
        }
      }

      console.log('筛选后数量:', items.length); // 按时间排序

      items.sort(function (a, b) {
        if (!a.createTime || !b.createTime) return 0;
        return new Date(b.createTime) - new Date(a.createTime);
      });
      this.allItems = items;
      this.total = items.length;
      console.log('最终列表数量:', this.allItems.length);
    },
    handleStatusChange: function handleStatusChange() {
      this.currentPage = 1;
      this.mergeItems();
    },
    handleTypeChange: function handleTypeChange() {
      this.currentPage = 1;
      this.mergeItems();
    },
    handlePageChange: function handlePageChange(page) {
      this.currentPage = page;
      this.loadAllData();
    },
    viewDetails: function viewDetails(item) {
      var _this4 = this;

      // 如果是售后申请，需要获取完整详情（包含订单、买家、卖家信息）
      if (item.type === 'afterSale') {
        this.loading = true;
        this.$api.getAdminAfterSaleDetail({
          id: item.id
        }).then(function (res) {
          if (res.status_code === 1 && res.data) {
            res.data.type = 'afterSale';
            _this4.currentItem = res.data;
            _this4.handleForm.handleResult = 1;
            _this4.handleForm.handleDescription = '';
            _this4.dialogVisible = true;
          } else {
            _this4.$message.error('获取售后申请详情失败');
          }
        }).catch(function (err) {
          console.error('获取售后申请详情失败', err);

          _this4.$message.error('获取售后申请详情失败');
        }).finally(function () {
          _this4.loading = false;
        });
      } else {
        // 交易纠纷直接使用列表数据
        this.currentItem = item;
        this.handleForm.handleResult = 1;
        this.handleForm.handleDescription = '';
        this.dialogVisible = true;
      }
    },
    canHandle: function canHandle(item) {
      if (item.type === 'dispute') {
        return item.disputeStatus === 0;
      } else {
        return item.applicationStatus === 3 || item.applicationStatus === 4;
      }
    },
    getItemStatusText: function getItemStatusText(item) {
      if (item.type === 'afterSale') {
        var statuses = {
          0: '待卖家审核',
          1: '卖家已同意',
          2: '卖家已拒绝',
          3: '待管理员审核（卖家同意）',
          4: '待管理员审核（卖家拒绝）',
          5: '管理员已同意退款',
          6: '管理员已驳回'
        };
        return statuses[item.applicationStatus] || '未知状态';
      } else {
        return this.getStatusText(item.disputeStatus);
      }
    },
    getItemStatusType: function getItemStatusType(item) {
      if (item.type === 'afterSale') {
        if (item.applicationStatus === 3 || item.applicationStatus === 4) return 'warning';
        if (item.applicationStatus === 5) return 'success';
        if (item.applicationStatus === 6) return 'danger';
        return 'info';
      } else {
        return this.getStatusType(item.disputeStatus);
      }
    },
    getApplicationTypeText: function getApplicationTypeText(type) {
      var types = {
        1: '质量问题',
        2: '描述不符',
        3: '未收到货',
        4: '其他'
      };
      return types[type] || '未知';
    },
    getDialogTitle: function getDialogTitle() {
      if (!this.currentItem) return '详情';

      if (this.currentItem.type === 'afterSale') {
        return this.canHandle(this.currentItem) ? '处理售后申请' : '查看售后申请详情';
      } else {
        return this.currentItem.disputeStatus === 0 ? '处理交易纠纷' : '查看纠纷详情';
      }
    },
    submitHandle: function submitHandle() {
      var _this5 = this;

      if (!this.handleForm.handleResult) {
        this.$message.warning('请选择处理结果');
        return;
      }

      this.$confirm('确认提交处理结果？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this5.$api.adminHandleDispute({
          id: _this5.currentItem.id,
          handleResult: _this5.handleForm.handleResult,
          handleDescription: _this5.handleForm.handleDescription
        }).then(function (res) {
          if (res.status_code === 1) {
            _this5.$message.success('处理成功');

            _this5.dialogVisible = false;

            _this5.loadAllData();
          } else {
            _this5.$message.error(res.msg || '处理失败');
          }
        }).catch(function (err) {
          console.error('处理纠纷失败', err);

          _this5.$message.error('处理失败');
        });
      }).catch(function () {});
    },
    submitAfterSaleHandle: function submitAfterSaleHandle() {
      var _this6 = this;

      if (!this.handleForm.handleResult) {
        this.$message.warning('请选择处理结果');
        return;
      }

      if (this.handleForm.handleResult === 2 && !this.handleForm.handleDescription) {
        this.$message.warning('驳回申请时必须填写驳回原因');
        return;
      }

      this.$confirm('确认提交处理结果？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        _this6.$api.adminReviewAfterSale({
          id: _this6.currentItem.id,
          reviewResult: _this6.handleForm.handleResult,
          reason: _this6.handleForm.handleDescription
        }).then(function (res) {
          if (res.status_code === 1) {
            _this6.$message.success('处理成功');

            _this6.dialogVisible = false;

            _this6.loadAllData();
          } else {
            _this6.$message.error(res.msg || '处理失败');
          }
        }).catch(function (err) {
          console.error('处理售后申请失败', err);

          _this6.$message.error('处理失败');
        });
      }).catch(function () {});
    },
    getDisputeTypeText: function getDisputeTypeText(type) {
      var types = {
        1: '商品描述不符',
        2: '商品质量问题',
        3: '未收到货',
        4: '退款问题',
        5: '其他'
      };
      return types[type] || '未知';
    },
    getStatusText: function getStatusText(status) {
      var statuses = {
        0: '待处理',
        1: '处理中',
        2: '已解决',
        3: '已关闭'
      };
      return statuses[status] || '未知';
    },
    getStatusType: function getStatusType(status) {
      var types = {
        0: 'warning',
        1: 'info',
        2: 'success',
        3: 'info'
      };
      return types[status] || '';
    },
    getHandleResultText: function getHandleResultText(result) {
      var results = {
        1: '退款给买家',
        2: '驳回申请',
        3: '要求退货退款',
        4: '警告用户',
        5: '其他'
      };
      return results[result] || '未知';
    },
    getOrderStatusText: function getOrderStatusText(status) {
      var statuses = {
        0: '待确认',
        1: '已确认',
        2: '已发货',
        3: '已完成',
        4: '已取消'
      };
      return statuses[status] || '未知';
    },
    getEvidenceImages: function getEvidenceImages(imagesStr) {
      if (!imagesStr) return [];

      try {
        return JSON.parse(imagesStr);
      } catch (e) {
        return [];
      }
    }
  }
};
