        },
        handleBannerClick(banner) {
            if (banner.type === 'item' && banner.item) {
                // ç‚¹å‡»å•†å“ï¼Œè·³è½¬åˆ°å•†å“è¯¦æƒ…
                this.toDetails(banner.item);
