/* 根容器 - 核心修复：背景图100%清晰显示 无任何叠加层 亮度不变！！！ */
.container {
  height: 100vh;
  width: 100vw;
  overflow: hidden;
  background-image: url("../../img/普通用户登录背景图.jpg");
  background-size: cover;
  background-position: center center;
  background-repeat: no-repeat;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #333;
}

/* 登录卡片主体 - 所有高级样式保留 毛玻璃+悬浮上浮+渐变阴影 不变 */
.login-card {
  width: 430px; /* 微调宽度，完美适配标题一行显示，不换行 */
  height: 470px;
  /* 毛玻璃通透效果保留，但是调更高透明度，不挡背景+更清爽 */
  background: rgba(255, 255, 255, 0.88);
  backdrop-filter: blur(6px);
  -webkit-backdrop-filter: blur(6px);
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(79, 192, 141, 0.12), 0 2px 10px rgba(79, 192, 141, 0.06);
  border: 1px solid rgba(255, 255, 255, 0.95);
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease-in-out;
  /* 响应式适配 */
  @media screen and (max-width: 500px) {
    width: 92%;
    height: auto;
    padding: 30px 0;
  }
}
/* 卡片悬浮上浮+阴影加深 高级交互保留 */
.login-card:hover {
  transform: translateY(-3px);
  box-shadow: 0 15px 40px rgba(79, 192, 141, 0.18), 0 3px 15px rgba(79, 192, 141, 0.09);
}

.login-form-box {
  width: 90%;
  height: 90%;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

/* ✅ 标题：校园二手交易商城 一行完美显示 不换行 核心调整 */
.login-title {
  text-align: center;
  font-size: 32px; /* 精准字号，刚好一行放下，不变形 */
  font-weight: 700;
  margin-bottom: 40px;
  letter-spacing: 2px;
  /* 渐变文字保留，点睛之笔 不变 */
  background: linear-gradient(120deg, #2d8259, #4fc08d, #67c29c);
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
}

/* 表单样式 */
.login-form {
  width: 100%;
}

/* 输入框 超级美化 渐变边框+hover光效 全部保留 */
.login-input {
  width: 100%;
  margin-bottom: 28px;
  border-radius: 12px;
  transition: all 0.3s ease;
  --el-input-border-radius: 12px;
  --el-input-focus-border-color: #4fc08d;
  --el-input-hover-border-color: #67c29c;
}

/* 登录按钮区域 */
.login-submit {
  width: 100%;
  display: flex;
  justify-content: center;
  margin-top: 15px;
  margin-bottom: 25px;
}
/* 渐变按钮+悬浮动效+点击缩放 全部保留 */
.login-btn {
  width: 100%;
  height: 52px;
  font-size: 18px;
  font-weight: 600;
	border-radius: 12px;
  letter-spacing: 2px;
  background: linear-gradient(90deg, #3ba674, #4fc08d) !important;
  border: none !important;
  box-shadow: 0 4px 12px rgba(79, 192, 141, 0.2);
  transition: all 0.3s ease;
}
.login-btn:hover {
  background: linear-gradient(90deg, #4fc08d, #3ba674) !important;
  transform: scale(1.02);
  box-shadow: 0 6px 18px rgba(79, 192, 141, 0.28);
}
.login-btn:active {
  transform: scale(0.98);
}

/* 注册/管理员链接 渐变文字+下划线动效 全部保留 */
.other-submit {
  display: flex;
  justify-content: space-between;
  padding: 0 15px;
}
.sign-in-text {
  color: #3ba674;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  letter-spacing: 1px;
  transition: all 0.3s ease;
  position: relative;
}
.sign-in-text::after {
  content: '';
  position: absolute;
  bottom: -2px;
	left: 0;
 	width: 0;
	height: 2px;
  background: linear-gradient(90deg, #3ba674, #4fc08d);
  transition: width 0.3s ease;
}
.sign-in-text:hover {
  background: linear-gradient(90deg, #2d8259, #4fc08d);
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
}
.sign-in-text:hover::after {
  width: 100%;
}

/* elementUI内置样式美化 全部保留 */
::v-deep .el-input__prefix {
  color: #4fc08d;
  font-size: 18px;
  padding-left: 5px;
}
::v-deep .el-input__inner {
  height: 52px;
  font-size: 17px;
  padding: 0 20px;
  border: 1.5px solid #e8f5e9;
  background: rgba(255, 255, 255, 0.95);
  transition: all 0.3s ease;
}
::v-deep .el-input__inner:focus {
  border-color: #4fc08d;
  box-shadow: 0 0 0 3px rgba(79, 192, 141, 0.1);
}
::v-deep .el-input__inner:hover {
  border-color: #67c29c;
}
::v-deep .el-input__suffix-inner {
  color: #67c29c;
  font-size: 16px;
}
